import '../../../../../html-css-template/css/HemocentroEHorario.css'
import { useCallback, useEffect, useState } from 'react';
import Swal from 'sweetalert2';
import { IHistoricoAgendamento, TarefasService } from '../../../../shared/sevice/api/tarefas/TarefasService';
import { ApiException } from '../../../../shared/sevice/api/ApiException';

interface IHorarioProps {
    onChange: () => void;
}

export const Horario: React.FC<IHorarioProps> = ({ onChange }) => {
    const [horario, setHorario] = useState<string>('');
    const [horarioDisponivel, setHorarioDisponivel] = useState<IHistoricoAgendamento[]>([]);

    sessionStorage.setItem('hora', horario);

    const vetorHora = [
        {
            id: 1,
            hora: '08:30:00'
        },
        {
            id: 2,
            hora: '09:00:00'
        },
        {
            id: 3,
            hora: '09:30:00'
        },
        {
            id: 4,
            hora: '10:00:00'
        },
        {
            id: 5,
            hora: '10:30:00'
        },
        {
            id: 6,
            hora: '11:00:00'
        },
        {
            id: 7,
            hora: '11:30:00'
        },
        {
            id: 8,
            hora: '13:00:00'
        },
        {
            id: 9,
            hora: '13:30:00'
        },
        {
            id: 10,
            hora: '14:00:00'
        },
        {
            id: 11,
            hora: '14:30:00'
        },
        {
            id: 12,
            hora: '15:00:00'
        },
    ];

    const vetorExemplo = [
        {
            id: 1,
            hora: 8.30,
            hospital: [
                {
                    idHospital: 1,
                    nome: 'Santa Cruz Azul',
                    cep: '042444000'
                },
                {
                    idHospital: 2,
                    nome: 'São Luiz',
                    cep: '042444001'
                }
            ]
        },
        {
            id: 2,
            hora: 11.00,
            hospital: {
                idHospital: 2,
                nome: 'São Luiz',
                cep: '042444001'
            }
        },
        {
            id: 3,
            hora: 9.30,
            hospital: {
                idHospital: 3,
                nome: 'São Camilo',
                cep: '042444002'
            }
        },
        {
            id: 4,
            hora: 13.00,
            hospital: {
                idHospital: 4,
                nome: 'São Camilo',
                cep: '042444002'
            }
        },
        {
            id: 5, hora: 14.30,
            hospital: {
                idHospital: 5,
                nome: 'São Camilo',
                cep: '042444002'
            }
        }
    ];

    const horarioIndisponivel = (hora: string) => {
        {/* Método find retornará o primeiro item em que a condição retorne verdadeira */ }
        TarefasService.getAllHistoricoAgendamento()
            .then((result) => {
                if (result instanceof ApiException) {
                    return alert(result.message);
                }
                else {
                    setHorarioDisponivel(result.data);
                    horarioDisponivel.find((item) => item.horario.getHours().toString() === hora);

                     if (horarioDisponivel) {
                        return '(Indisponível)';
                    } else {
                        return '(Disponível)';
                    }
                }
            });
    };



    const showChosenTime = (message: string) => {
        const Toast = Swal.mixin({
            toast: true,
            position: "top-end",
            showConfirmButton: false,
            timer: 5000,
            timerProgressBar: true,
            didOpen: (toast) => {
                toast.onmouseenter = Swal.stopTimer;
                toast.onmouseleave = Swal.resumeTimer;
            }
        });

        Toast.fire({
            icon: "info",
            title: message
        });
    };

    const showChosenWrongTime = (message: string) => {
        const Toast = Swal.mixin({
            toast: true,
            position: "top-end",
            showConfirmButton: false,
            timer: 5000,
            timerProgressBar: true,
            didOpen: (toast) => {
                toast.onmouseenter = Swal.stopTimer;
                toast.onmouseleave = Swal.resumeTimer;
            }
        });

        Toast.fire({
            icon: "error",
            title: message
        });
    };

    const horaEscolhida = useCallback((hora: string) => {
        if (horarioIndisponivel(hora) === '(Disponível)') {
            setHorario(hora);
            showChosenTime("Agora, finalize selecionando o botão de agendar.");
        }
        else {
            showChosenWrongTime("Horário indisponível, selecione outro.");
        }
    }, []);

    return (
        <>
            <div className="caixaHemo">
                <div className="formularioAgenda">
                    <div className="headerHomo">
                        <h1 className="rowdies">HORÁRIO</h1>
                        <button className="btnFechar bg-azulClaro" onClick={onChange}>
                            <p className="roboto sbold-24">x</p>
                        </button>
                    </div>
                    <div className="divisor">
                        <span className='linhaDivisoria'></span>
                    </div>
                    <div className="historicoHorario">
                        {vetorHora.map((vetor) => {
                            return <div className="caixaHora" key={vetor.id}>
                                <h3 onClick={() => horaEscolhida(vetor.hora)} className="hora roboto regular-20">
                                    <p>{vetor.hora}h</p>
                                    <p>{horarioIndisponivel(vetor.hora)}</p>
                                </h3>
                            </div>
                        })}
                    </div>
                </div>
            </div>
        </>
    );
}
