import React, { useCallback, useEffect, useState } from "react";
import { ITarefa, TarefasService } from "../../shared/sevice/api/tarefas/TarefasService";
import { ApiException } from "../../shared/sevice/api/ApiException";

export const Dashboard = () => {
    const [lista, setLista] = useState<ITarefa[]>([]);

    useEffect(() => {
        TarefasService.getAll()
            .then((result) => {
                if (result instanceof ApiException) {
                    alert(result.message);
                }
                else {
                    setLista(result);
                }
            })
    }, []);

    const handleInputKeyDown: React.KeyboardEventHandler<HTMLInputElement> = useCallback((e) => {
        if (e.key === 'Enter') {
            if (e.currentTarget.value.trim().length === 0) return;

            const value = e.currentTarget.value;

            e.currentTarget.value = '';

            if (lista.some((listItem) => listItem.title === value)) return;

            TarefasService.create({ title: value, isCompleted: false })
                .then((result) => {
                    if (result instanceof ApiException) {
                        alert(result.message);
                    }
                    else {
                        setLista((oldList) => [...oldList, result]);
                    }
                });
        }
    }, [lista]);

    const handleToggleComplete = useCallback((id: number) => {
        const tarefaToUpdate = lista.find((tarefa) => tarefa.id === id);
        if (!tarefaToUpdate) return;

        TarefasService.updateById(id, { ...tarefaToUpdate, isCompleted: !tarefaToUpdate.isCompleted })
            .then((result) => {
                if (result instanceof ApiException) {
                    alert(result.message);
                }
                else {
                    setLista(oldList => {
                        return oldList.map(oldListItem => {
                            if (oldListItem.id === id) return result;
                            return oldListItem;
                        });
                    });
                }
            }
            );
    }, [lista]);

    const handleDelete = useCallback((id: number) => {
        TarefasService.deleteById(id)
            .then((result) => {
                if (result instanceof ApiException) {
                    alert(result.message);
                }
                else {
                    setLista(oldList => {
                        return oldList.filter(oldListItem => oldListItem.id !== id);
                    });
                }
            }
            );
    }, []);

    return (
        <div>
            <div className="img">
                <img src="../assets/ONDA - CADASTRO.svg" alt="" />
            </div>

            <header>
                <div className="componente">
                    <img src="../assets/LogoVitai.png" alt="Vitae" />
                    <a className="roboto" href="">Home</a>
                </div>

                <div className="nextPage">
                    <div>
                        <h3>Já tem conta ?</h3>
                        <h3>Então vamos logar!</h3>
                    </div>

                    <button className="btn logar bold-20">
                        Logar
                        <img src="../assets/BackArrow.png" alt="" />
                    </button>
                </div>
            </header>

            <div className="container">
                <div className="formulario">
                    <div className="cadastro">
                        <h1>Cadastre-se</h1>
                        <h1 className="text-title">Bem vindo!!</h1>
                        <h1 className="text-title">Cadastre suas informações pessoais</h1>
                    </div>
                    <div className="inputs">
                        <input className="input-size" type="text" placeholder="Email" />
                        <input className="input-size" type="text" placeholder="Senha" />
                        <input className="input-size" type="text" placeholder="Confirmar senha" />
                    </div>
                    <div className="button">
                        <button className="btn cadastrar bold-20">
                            Cadastrar
                            <img src="../assets/BackArrow.png" alt="" />
                        </button>
                    </div>
                </div>
                <img className="imgDoe" src="../assets/WorldBloodDonorDay.png" alt="" />
            </div>
        </div>
    );
}
